import { useState, useEffect } from 'react';
import { Link, useLocation } from 'wouter';
import { Button } from '@/components/ui/button';
import { Sheet, SheetContent, SheetTrigger } from '@/components/ui/sheet';
import { Separator } from '@/components/ui/separator';
import { Menu, Sparkles, ChevronDown } from 'lucide-react';

const Navbar = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [location] = useLocation();

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 100);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const navLinks = [
    { href: '/', label: 'Home' },
    { href: '/about', label: 'About' },
    { 
      href: '/services', 
      label: 'Services',
      hasDropdown: true,
      subLinks: [
        { href: '/services/hair', label: 'Hair Services' },
        { href: '/services/skin', label: 'Skin Treatments' },
        { href: '/services/nails', label: 'Nail Care' },
        { href: '/services/makeup', label: 'Makeup & Beauty' },
        { href: '/services/spa', label: 'Spa & Relaxation' },
        { href: '/services/bridal', label: 'Bridal Packages' },
      ]
    },
    { href: '/pricing', label: 'Pricing' },
    { href: '/gallery', label: 'Gallery' },
    { href: '/contact', label: 'Contact' },
  ];

  return (
    <>
      <nav 
        className={`fixed top-0 left-0 w-full z-40 transition-all duration-300 ${
          isScrolled ? 'navbar-scrolled' : ''
        }`}
        data-testid="navbar"
      >
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-20">
            {/* Logo */}
            <Link href="/" className="flex items-center space-x-3" data-testid="logo-link">
              <Sparkles className="text-3xl text-primary" />
              <span className="text-2xl font-playfair font-bold text-text-dark">Luxe Beauty</span>
            </Link>

            {/* Desktop Menu */}
            <div className="hidden lg:flex items-center space-x-8">
              {navLinks.map((link) => (
                <div key={link.href} className="relative group">
                  <Link 
                    href={link.href}
                    className={`text-text-dark hover:text-primary transition-colors font-medium flex items-center ${
                      location === link.href ? 'text-primary' : ''
                    }`}
                    data-testid={`nav-link-${link.label.toLowerCase()}`}
                  >
                    {link.label}
                    {link.hasDropdown && <ChevronDown className="ml-1 h-4 w-4" />}
                  </Link>

                  {/* Dropdown Menu */}
                  {link.hasDropdown && (
                    <div className="absolute top-full left-0 mt-2 w-56 bg-white rounded-lg shadow-xl opacity-0 invisible group-hover:opacity-100 group-hover:visible transition-all duration-300 z-50">
                      {link.subLinks?.map((subLink) => (
                        <Link 
                          key={subLink.href}
                          href={subLink.href}
                          className="block px-4 py-3 hover:bg-bg-light transition-colors text-text-dark hover:text-primary"
                          data-testid={`nav-sublink-${subLink.label.toLowerCase().replace(/\s+/g, '-')}`}
                        >
                          {subLink.label}
                        </Link>
                      ))}
                    </div>
                  )}
                </div>
              ))}
            </div>

            {/* CTA Button */}
            <div className="hidden lg:block">
              <Link href="/booking">
                <Button className="btn-gradient" data-testid="book-appointment-button">
                  <span>Book Appointment</span>
                </Button>
              </Link>
            </div>

            {/* Mobile Menu Button */}
            <Sheet open={isMobileMenuOpen} onOpenChange={setIsMobileMenuOpen}>
              <SheetTrigger asChild>
                <Button 
                  variant="ghost" 
                  size="icon" 
                  className="lg:hidden"
                  data-testid="mobile-menu-button"
                >
                  <Menu className="h-6 w-6" />
                </Button>
              </SheetTrigger>
              <SheetContent side="right" className="w-[300px] sm:w-[400px]">
                <div className="flex items-center space-x-3 mb-8">
                  <Sparkles className="text-2xl text-primary" />
                  <span className="text-xl font-playfair font-bold text-text-dark">Luxe Beauty</span>
                </div>

                <div className="space-y-4">
                  {navLinks.map((link) => (
                    <div key={link.href}>
                      <Link 
                        href={link.href}
                        className={`block text-text-dark hover:text-primary transition-colors font-medium ${
                          location === link.href ? 'text-primary' : ''
                        }`}
                        onClick={() => setIsMobileMenuOpen(false)}
                        data-testid={`mobile-nav-link-${link.label.toLowerCase()}`}
                      >
                        {link.label}
                      </Link>
                      {link.hasDropdown && (
                        <div className="pl-4 mt-2 space-y-2">
                          {link.subLinks?.map((subLink) => (
                            <Link 
                              key={subLink.href}
                              href={subLink.href}
                              className="block text-sm text-gray-600 hover:text-primary transition-colors"
                              onClick={() => setIsMobileMenuOpen(false)}
                              data-testid={`mobile-nav-sublink-${subLink.label.toLowerCase().replace(/\s+/g, '-')}`}
                            >
                              {subLink.label}
                            </Link>
                          ))}
                        </div>
                      )}
                      <Separator className="mt-4" />
                    </div>
                  ))}
                  <Link href="/booking" onClick={() => setIsMobileMenuOpen(false)}>
                    <Button className="w-full btn-gradient mt-6" data-testid="mobile-book-appointment-button">
                      <span>Book Appointment</span>
                    </Button>
                  </Link>
                </div>
              </SheetContent>
            </Sheet>
          </div>
        </div>
      </nav>
    </>
  );
};

export default Navbar;

client/src/components/ui/CounterSection..content:
import { useEffect, useRef, useState } from 'react';
import ScrollReveal from './ScrollReveal';

interface CounterProps {
  target: number;
  label: string;
  delay?: number;
}

const Counter = ({ target, label, delay = 0 }: CounterProps) => {
  const [count, setCount] = useState(0);
  const [hasAnimated, setHasAnimated] = useState(false);
  const counterRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting && !hasAnimated) {
          setHasAnimated(true);

          setTimeout(() => {
            const duration = 2000;
            const increment = target / (duration / 16);
            let current = 0;

            const updateCounter = () => {
              current += increment;
              if (current < target) {
                setCount(Math.floor(current));
                requestAnimationFrame(updateCounter);
              } else {
                setCount(target);
              }
            };

            updateCounter();
          }, delay);
        }
      },
      { threshold: 0.5 }
    );

    if (counterRef.current) {
      observer.observe(counterRef.current);
    }

    return () => {
      if (counterRef.current) {
        observer.unobserve(counterRef.current);
      }
    };
  }, [target, hasAnimated, delay]);

  return (
    <div ref={counterRef} className="text-center" data-testid={`counter-${label.toLowerCase().replace(/\s+/g, '-')}`}>
      <div className="text-5xl font-bold text-gold mb-2 font-playfair" data-testid={`counter-value-${label.toLowerCase().replace(/\s+/g, '-')}`}>
        {count.toLocaleString()}
      </div>
      <p className="text-gray-600 font-semibold" data-testid={`counter-label-${label.toLowerCase().replace(/\s+/g, '-')}`}>
        {label}
      </p>
    </div>
  );
};

const CounterSection = () => {
  const stats = [
    { target: 15, label: 'Years of Excellence' },
    { target: 50000, label: 'Happy Clients' },
    { target: 25, label: 'Expert Professionals' },
    { target: 12, label: 'Industry Awards' },
  ];

  return (
    <section className="py-20 bg-white" data-testid="counter-section">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="grid md:grid-cols-4 gap-12">
          {stats.map((stat, index) => (
            <ScrollReveal key={stat.label} delay={index * 100}>
              <Counter 
                target={stat.target} 
                label={stat.label}
              />
            </ScrollReveal>
          ))}
        </div>
      </div>
    </section>
  );
};

export default CounterSection;

